sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
	var SNTestStartDate= g_form.getValue('u_test_start_date');
   var SNTestEndDate= g_form.getValue('u_test_end_date');
	var SNTestCurrentDate= g_form.getValue('u_test_current_date');

	if((SNTestEndDate<SNTestStartDate) || (SNTestEndDate==SNTestStartDate))
		{
			alert("End date cannot be less than or equal to the Start date");
			return false;
		}
	if(SNTestStartDate<SNTestCurrentDate)
		{
			alert("Start Date cannot be older than today");
			return false;
		}

}
